import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { Link } from 'react-router-dom'
import'./login.css'

const Login = () => {
const [login,setlogin]=useState('')
const [userpasward,setuserpasward]=useState('');
const [showpassward,setshowpasseard]=useState('')
const navigate = useNavigate();

let isvalid=false
function click(event) {

  const value = JSON.parse(localStorage.getItem('newUser')) || [];

  console.log(value);

  for(let key of value){

    if(key.username === login && key.password === userpasward){
      isvalid = true;
    }


    if(isvalid){
     navigate('/About');
    }
    else{
      alert('invalid user name')
    }
  }

}
  return (
    <form onSubmit={click}>
<div className='container-fluid height'>
        <div className='d-flex align-items-center m-auto height-one '>
        <div className='border rounded width text-center color'>
      <div class="mb-3">
  <label for="validationDefault01" class="form-label">User Name</label>
  <input type="text" class="form-control" id="validationDefault01" value={login} onChange={(e=>setlogin(e.target.value))} placeholder="User Name" required/>
</div>
<div class="mb-3 d-flex align-items-center">
  <div>
  <label for="validationDefault02" class="form-label">Passward</label>
  <input type="text" class="form-control" id="validationDefault02" value={userpasward} onChange={(e=>setuserpasward(e.target.value))} placeholder="Passward" required />
  </div>
  <button className='pb-2 text-center px-3 rounded bg-white border-0  mt-4'>helo</button>
</div>
<button className='px-5 py-2 mt-2 rounded'>Login </button>
<div className='pt-4'><p>Did not have a account?<Link to='/Sign'> Sign Up</Link></p></div>
    </div>
    </div>
    </div>
    </form>
  )
}
export default Login
