import React, { useState } from 'react'
import {  useNavigate ,useLocation} from 'react-router-dom'
import './add.css'

const Add = () => {

  const [incresecount,setincresecount]=useState(0)
  const [add,setadd]=useState([])
  const location=useLocation()
  const passeddata=location.state
 const data=passeddata
 const navigate=useNavigate()
 const additem = () => {
  const newitems = { ...data, count: incresecount };
  setadd([...add, newitems]);
  setincresecount(0);
  console.log('Data to be Passed:', add); // Log the data before navigation
  navigate('/Card', { state: add });
};



  function increse(){
    setincresecount(incresecount+1)
  }
  function decrese(){
    if(incresecount>0){
      setincresecount(incresecount-1)
    }
    useEffect(() => {
      if (add.length > 0) {
        navigate('/Card', { state: add });
      }
    }, [add]);

    return

  }
  return (
    <div className='d-flex height-one align-items-center '>
      <div className='col-6  justify-content-center  d-flex'>
      <img alt='#' src={passeddata.img} height={500} width={500}></img>
      </div>
      <div className='col-6 justify-content-center d-flex '>
        <div>
        <h1>TITLE:{passeddata.title}</h1>
      <h4>{passeddata.discription}</h4>
      <h1>Price: {passeddata.price}</h1>
      <div className='d-flex'>
      <button onClick={increse}>+</button>
      <input value={incresecount}/>
      <button onClick={decrese}>-</button>
      </div>
      <div>
      <button className='bg-primary px-3 py-2' onClick={additem} >Add to cart </button>
      </div>

        </div>

      </div>

  </div>
  )
}

export default Add;
