import React, { useState } from 'react'
import obj from './Data.js'
import { Link } from 'react-router-dom'


const Watches = ({ addtocart, data }) => {
  function handleAddToCart(item) {
    addtocart(item);
  }

  return (
    <div className='container'>
      <div className='d-flex flex-wrap justify-content-center '>
        {pbj.map((a, index) => (
          <div key={index} className='box border mx-4 my-3 ' style={{ flex: '0 0 33.33%', maxWidth: '25.33%' }}>
            <div>
              <img className='d-flex justify-content-center' src={a.img} height={300} width={300} alt='#' />
              <div>{a.title}</div>
              <div>
                <h3> {a.discription}</h3>
              </div>
              <div>Rs:{a.price}</div>
              <button className='px-3 py-2 bg-primary text-white' onClick={() => handleAddToCart(a)}>
                Add to Cart
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Watches
