import React, { useState } from 'react'
import'./login.css'
const Login = () => {
const [login,setlogin]=useState('')
const [userpasward,setuserpasward]=useState('');
const [reult,setresult]=useState('')
function click(event) {
  event.preventDefault();
  const value = JSON.parse(localStorage.getItem('newUser')) || [];
  for(let key of value){
    if(key.username===login && key.password===userpasward){
      setresult('login sucessfully')
      return
    }
    else{
      setresult('invalid user name')
      return
    }
  }
}
  return (
    <form onSubmit={click}>
<div className='container-fluid height'>
        <div className='d-flex align-items-center m-auto height-one '>
        <div className='border rounded width text-center color'>
      <div class="mb-3">
        <p>{reult}</p>
  <label for="formGroupExampleInput" class="form-label">User Name</label>
  <input type="text" class="form-control" id="validationDefault01" value={login} onChange={(e=>setlogin(e.target.value))} placeholder="User Name"/>
</div>
<div class="mb-3">
  <label for="formGroupExampleInput2" class="form-label">Passward</label>
  <input type="text" class="form-control" id="formGroupExampleInput2" value={userpasward} onChange={(e=>setuserpasward(e.target.value))} placeholder="Passward"/>
</div>
<button className='px-5 py-2 mt-2 rounded'>Login </button>
<div className='pt-4'><p>Did not have a account?<a href='#'> Sign Up</a></p></div>
    </div>
        </div>
    </div>
    </form>
  )
}
export default Login
